import Link from 'next/link';

export default function Home() {
  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-50 text-center p-4">
      <h1 className="text-4xl font-bold mb-4 text-purple-600">Bem-vindo ao VUNOCode</h1>
      <p className="mb-6 text-gray-700">Uma plataforma para acelerar sua criação digital de forma automatizada e inteligente.</p>
      <div className="flex space-x-4">
        <Link href="/login" className="bg-purple-600 text-white px-4 py-2 rounded hover:bg-purple-700 transition">Login</Link>
        <Link href="/cadastro" className="bg-gray-200 px-4 py-2 rounded hover:bg-gray-300 transition">Cadastre-se</Link>
      </div>
      <div className="mt-8 text-sm text-gray-500 max-w-2xl">
        <p>O VUNOCode automatiza a criação de estruturas para seus projetos. Gere páginas, componentes e estilos em minutos!</p>
      </div>
    </div>
  );
}

import { useState } from 'react';

export default function Login() {
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Email: ${email}\nSenha: ${senha}`);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-purple-400 to-purple-700">
      <form onSubmit={handleSubmit} className="bg-white p-8 rounded-lg shadow-lg transform hover:scale-105 transition w-80">
        <h2 className="text-2xl font-bold mb-6 text-center">Login</h2>
        <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} className="w-full p-2 mb-4 border rounded" required />
        <input type="password" placeholder="Senha" value={senha} onChange={(e) => setSenha(e.target.value)} className="w-full p-2 mb-4 border rounded" required />
        <button type="submit" className="w-full bg-purple-600 text-white p-2 rounded hover:bg-purple-700">Entrar</button>
      </form>
    </div>
  );
}

import { useState } from 'react';

export default function Cadastro() {
  const [formData, setFormData] = useState({ nome: '', email: '', tipo: 'usuario', senha: '', confirmSenha: '', aceito: false });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({ ...prev, [name]: type === 'checkbox' ? checked : value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (formData.senha !== formData.confirmSenha) {
      alert('As senhas não coincidem!');
      return;
    }
    if (!formData.aceito) {
      alert('Você precisa aceitar os termos.');
      return;
    }
    alert(`Cadastro realizado para: ${formData.nome}`);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50">
      <form onSubmit={handleSubmit} className="bg-white p-8 rounded-lg shadow-lg w-96">
        <h2 className="text-2xl font-bold mb-6 text-center">Cadastro</h2>
        <input type="text" name="nome" placeholder="Nome completo" onChange={handleChange} className="w-full p-2 mb-4 border rounded" required />
        <input type="email" name="email" placeholder="Email" onChange={handleChange} className="w-full p-2 mb-4 border rounded" required />
        <select name="tipo" onChange={handleChange} className="w-full p-2 mb-4 border rounded">
          <option value="usuario">Usuário</option>
          <option value="empresa">Empresa</option>
        </select>
        <input type="password" name="senha" placeholder="Senha (8+ caracteres, 1 maiúscula)" onChange={handleChange} className="w-full p-2 mb-4 border rounded" required minLength={8} />
        <input type="password" name="confirmSenha" placeholder="Confirmar senha" onChange={handleChange} className="w-full p-2 mb-4 border rounded" required />
        <label className="flex items-center mb-4 text-sm">
          <input type="checkbox" name="aceito" onChange={handleChange} className="mr-2" /> Aceito os Termos de Uso
        </label>
        <button type="submit" className="w-full bg-purple-600 text-white p-2 rounded hover:bg-purple-700">Cadastrar</button>
      </form>
    </div>
  );
}

